<script>
  // Update date/time every second
  function updateDateTime() {
    const now = new Date();
    const options = { 
      weekday: 'long', 
      day: '2-digit', 
      month: '2-digit', 
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    };
    document.getElementById('currentDateTime').textContent = now.toLocaleDateString('en-US', options);
  }

  // Initial call
  updateDateTime();
  
  // Update every second
  setInterval(updateDateTime, 1000);

